mapper.select:
SELECT * from merchant m LEFT JOIN zsl_trace z on z.trace_business_id = m.merchant_id where z.trace_handle_status = 1 and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') <= date_format(#{endDate},'%Y-%m-%e %H:%i:%S') and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') >= date_format(#{startDate},'%Y-%m-%e %H:%i:%S')
and m.alliance_business_id is null
and m.alliance_business_id = #{companyId}

SELECT m.merchant_id,m.merchant_name, sum( if( z.trace_handle_status = 1 and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') <= date_format(#{endDate},'%Y-%m-%e %H:%i:%S') and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') >= date_format(#{startDate},'%Y-%m-%e %H:%i:%S')
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) = #{companyId}
,z.trace_apply_count ,0 ) ) as apply_count from merchant m LEFT JOIN zsl_trace z on z.trace_business_id = m.merchant_id
m.alliance_business_id is null
m.alliance_business_id = #{companyId}
GROUP BY m.merchant_id,m.merchant_name ORDER BY apply_count desc

SELECT z.trace_id, z.trace_apply_date, m.merchant_name, z.trace_apply_count, IFNULL(z.trace_code_number,'') as trace_code_number, IFNULL(z.trace_refuse_remark,'') as trace_refuse_remark , case z.trace_handle_status when 2 then '待审核' when 1 then '已通过' when 3 then '已撤回' ELSE '已拒绝' END as handle_status, ( select mpg.merchant_logo_url from merchant_papers_group mpg where mpg.merchant_id = m.merchant_id ) as merchant_logo FROM zsl_trace z LEFT JOIN merchant m on m.merchant_id = z.trace_business_id where m.merchant_status = 0
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) = #{allianceBusinessId}
and z.trace_business_id = #{merchantId}
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) is null and z.trace_handle_status = 2
ORDER BY z.trace_apply_date desc

select z.trace_id, z.trace_code_number, z.trace_company_name, z.trace_business_id, z.trace_apply_type, z.trace_handle_status, z.trace_apply_date, z.trace_review_date, z.trace_enable_count, z.trace_refuse_remark, z.trace_apply_count, z.trace_back4, m.* from zsl_trace z left join merchant m on m.merchant_id = z.trace_business_id where m.merchant_status = 0
and z.trace_back4 is null or z.trace_back4 = '' or z.trace_back4 = #{queryParam.isMiniProgram}
and z.trace_business_id = #{queryParam.merchantId}
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) = #{queryParam.allianceId}
AND z.trace_code_number like concat("%",#{queryParam.traceCodeNumber},"%")
AND z.trace_company_name like concat("%",#{queryParam.traceCompanyName},"%")
AND z.trace_handle_status = #{queryParam.traceHandleStatus}
AND z.trace_apply_type = #{queryParam.traceApplyType}
AND m.merchant_name like concat("%",#{queryParam.merchantName},"%")
AND date_format(z.trace_apply_date,'%Y-%m-%e %H:%i:%S') >= date_format(#{queryParam.traceApplyStartDate1},'%Y-%m-%e %H:%i:%S')
AND date_format(z.trace_apply_date,'%Y-%m-%e %H:%i:%S') <= date_format(#{queryParam.traceApplyEndDate1},'%Y-%m-%e %H:%i:%S')

SELECT count(*) FROM code_trace_integral_log cl LEFT JOIN zsl_trace_subcode ze ON ze.id = cl.function_id LEFT JOIN zsl_trace z ON z.trace_code_number = ze.trace_code_number where TO_DAYS(NOW()) - TO_DAYS(create_time) = 1
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) = #{companyId}
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) is null

SELECT count(*) FROM code_trace_integral_log cl LEFT JOIN zsl_trace_subcode ze ON ze.id = cl.function_id LEFT JOIN zsl_trace z ON z.trace_code_number = ze.trace_code_number where 1 = 1
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) = #{companyId}
and (SELECT alliance_business_id FROM `merchant` where merchant_id = z.trace_business_id) is null
