mapper.select:
SELECT m.merchant_id,m.merchant_name, sum( if( z.trace_handle_status = 1 and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') <= date_format(#{endDate},'%Y-%m-%e %H:%i:%S') and date_format(z.trace_review_date,'%Y-%m-%e %H:%i:%S') >= date_format(#{startDate},'%Y-%m-%e %H:%i:%S')
and z.trace_back2 = #{companyId}
,z.trace_apply_count ,0 ) ) as apply_count from merchant m LEFT JOIN zsl_trace z on z.trace_business_id = m.merchant_id
m.alliance_business_id is null
m.alliance_business_id = #{companyId}
GROUP BY m.merchant_id,m.merchant_name ORDER BY apply_count desc

SELECT z.trace_id, z.trace_apply_date, m.merchant_name, z.trace_apply_count, IFNULL(z.trace_code_number,'') as trace_code_number, IFNULL(z.trace_refuse_remark,'') as trace_refuse_remark , case z.trace_handle_status when 2 then '待审核' when 1 then '已通过' when 3 then '已撤回' ELSE '已拒绝' END as handle_status, ( select mpg.merchant_logo_url from merchant_papers_group mpg where mpg.merchant_id = m.merchant_id ) as merchant_logo FROM zsl_trace z LEFT JOIN merchant m on m.merchant_id = z.trace_business_id
z.trace_back2 = #{allianceBusinessId}
z.trace_business_id = #{merchantId}
z.trace_back2 is null and z.trace_handle_status = 2
ORDER BY z.trace_apply_date desc

select z.trace_id, z.trace_code_number, z.trace_company_name, z.trace_apply_type, z.trace_handle_status, z.trace_apply_date, z.trace_review_date, z.trace_enable_count, z.trace_refuse_remark, z.trace_apply_count, m.* from zsl_trace z left join merchant m on m.merchant_id = z.trace_business_id where m.merchant_status = 0
and z.trace_back4 is null or z.trace_back4 = '' or z.trace_back4 = #{queryParam.isMiniProgram}
and z.trace_back2 is null
and z.trace_business_id = #{queryParam.merchantId}
and z.trace_back2 = #{queryParam.allianceId}
AND z.trace_code_number like concat("%",#{queryParam.traceCodeNumber},"%")
AND z.trace_company_name like concat("%",#{queryParam.traceCompanyName},"%")
AND z.trace_handle_status = #{queryParam.traceHandleStatus}
AND z.trace_apply_type = #{queryParam.traceApplyType}
AND m.merchant_name like concat("%",#{queryParam.merchantName},"%")
AND date_format(z.trace_apply_date,'%Y-%m-%e %H:%i:%S') >= date_format(#{queryParam.traceApplyStartDate},'%Y-%m-%e %H:%i:%S') AND date_format(z.trace_apply_date,'%Y-%m-%e %H:%i:%S') <= date_format(#{queryParam.traceApplyEndDate},'%Y-%m-%e %H:%i:%S')
